import { Component, OnInit } from '@angular/core';
import { environment } from "../../environments/environment";
import {ajax} from "rxjs/ajax";
import {data} from '../data';
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";
import { Blog } from "../entity/blog";
import {BlogService} from "../service/blog.service";

const delimeter = '----------!!##!!----------';
const delimeter2 = '==========!!##!!==========';


@Component({
  selector: 'app-layout',
  templateUrl: './layout.component.html',
  styleUrls: ['./layout.component.css'],
  providers: [BlogService]
})
export class LayoutComponent implements OnInit {

  blogs = null;

  constructor(private blogService: BlogService) {
    const {owner, repo} = this.blogService.getDefaultOwnerAndRepo();
    this.blogService.getBlogs$(owner, repo)
      .subscribe(blogs => {
        this.blogs = blogs;

        const validBlogs = this.blogs.filter(d => !d.markdownRendered);
        const markdownText = validBlogs.map(d => {
          if (d.meta && d.meta.summary) {
            return [d.meta.summary, d.body].join(delimeter);
          } else {
            return d.body;
          }
        }).join(delimeter2);

        if (markdownText) {
          // render summaries markdown asynchronously
          this.blogService.renderMarkdown$(markdownText)
            .subscribe(d => {
              const summaries = d.split(delimeter2);
              validBlogs.forEach((blog, i) => {
                let [html1, html2] = summaries[i].split(delimeter);
                if (html1 && html2) {
                  blog.meta.summary = html1;
                  blog.body = html2;
                } else if (html1){
                  blog.body = html1;
                }

                blog.markdownRendered = true;
              });
            });
        }
      });
  }

  ngOnInit(): void {
  }



}
<nz-layout>
  <nz-layout>
    <nz-layout class="inner-layout">
      <nz-breadcrumb>
        <nz-breadcrumb-item>timeline</nz-breadcrumb-item>
        <nz-breadcrumb-item>dev</nz-breadcrumb-item>
        <nz-breadcrumb-item>deeplearning</nz-breadcrumb-item>
        <nz-breadcrumb-item>other</nz-breadcrumb-item>
      </nz-breadcrumb>
      <nz-content>
        <h3>{{owner}}.{{repo}}</h3>
        <div *ngIf="blogs">
          <div *ngFor="let blog of blogs">
            <a class="blog-title-link" >
              <h3>{{ blog }}</h3>
            </a>
<!--            <span>{{ blog.created_at | date:'yyyy-MM-dd' }}</span>-->
            <hr/>
          </div>
        </div>
      </nz-content>
    </nz-layout>
  </nz-layout>
  <nz-footer>Keep clam and carry on</nz-footer>
</nz-layout>
